import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/Services/auth.service';

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent implements OnInit {
  
  public form = {
    email: null,
    password: null
  };
  public error:any = null;
  

  constructor(private Auth: AuthService) { }
  onSubmit() {
   return this.http.post('http://127.0.0.1:8000/api/auth/login', this.form)
    this.Auth.subscribe({
      next: (data) => { console.log(data) },
      error: (err) => { this.handleError(err) },
    });

  }
  
  handleError(error:any) {
    this.error = error.error.error;
  }
  ngOnInit() {
  }
}
<div class="mt-4 col-8 offset-2">
    <div class="card">
      <div class="card-header">Login Here</div>
      <div class="card-body">
        <form #loginForm=ngForm (ngSubmit)="onSubmit()">
  
          <div  [hidden]="!error">
            {{error}}
          </div>
          <div class="form-group row">
            <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label>
            <div class="col-sm-10">
              <input type="email" name="email" class="form-control" id="inputEmail3" placeholder="Email" [(ngModel)]="form.email" required>
            </div>
          </div>
  
          <div class="form-group row">
            <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label>
            <div class="col-sm-10">
              <input type="password" name="password" class="form-control" id="inputPassword3" placeholder="Password" [(ngModel)]="form.password"
                required>
            </div>
          </div>
  
          <div class="form-group row">
            <div class="col-sm-10 offset-2">
  
              <button type="submit" class="btn btn-primary" [disabled]="!loginForm.valid">Sign in</button>
  
              <a routerLink="/signup" class="btn btn-info float-right">Sign Up</a>
  
            </div>
          </div>
          <small class=float-right>
            <a routerLink="/request-password-reset">Reset Password</a>
          </small>
        </form>
      </div>
    </div>
  </div>
