import { SnotifyModule } from 'ng-snotify';
import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/Services/auth.service';

@Component({
  selector: 'app-request-reset',
  templateUrl: './request-reset.component.html',
  styleUrls: ['./request-reset.component.css']
})
export class RequestResetComponent implements OnInit {
  public form = {
    email: null,
 }
 constructor(private Auth: AuthService,
  notify: SnotifyModule) { }


  onSubmit(){

 }

  ngOnInit(): void {
    this.Auth.sendPasswordResetLink(this.form).subscribe({
      next: (data) => {console.log(data) },
      error: this.notify.error(err)
    });
  }

  handelResonse(res: any){
   this.form.email = null;
  }

}
<div class=" w-[500px] dark:bg-black dark:bg-opacity-90
bg-white p-5 absolute shadow-lg rounded-2xl">
<div class="cursor-pointer justify-between m-0">
  <a class="col-span-1"><i class="fa-regular fa-x text-l dark:text-white"></i>
  </a>
</div>
<div class="flex justify-center p-0">
  <a href="#" class="text-main-blue rounded-full ml-1 pt-1 m-3 hover:bg-main-blue_0 transition-colors">
      <i class="fa-brands fa-twitter text-5xl dark:text-white"></i>
  </a>
</div>
<h1 class="text-3xl font-bold dark:text-white mb-5">Find your Twitter account</h1>
<p class="text-gray-600">Enter the email, phone number, or username associated with your account to change your password.</p>
<form #RequestResetForm=ngForm (ngSubmit)="onSubmit()">

<div class="mb-3">
            <input [(ngModel)]="form.email" name="email" required class="relative rounded block w-full h-[60px] appearance-none border border-gray-300  dark:border-gray-500 px-3 py-2 
            text-gray-900 placeholder-gray-500 focus:z-10 focus:border-indigo-100
            focus:outline-none focus:ring-indigo-500 text-xl dark:bg-black dark:focus:border-blue-400 dark:focus:border-blue-400 "
             placeholder="Email">
</div>


<div>
<p class="text-gray-700 dark:text-gray-500">
  <a  routerLink="request-password-reset" class="text-blue-600 dark:text-blue-800 dark:hover:text-blue-400">Forgot password?</a>
</p>
</div>
<button [disabled]="!RequestResetForm.valid" type="submit" id="next" class="rounded-full flex mt-[50px] font-bold dark:bg-opacity-60
  w-full justify-center border border-transparent 
  bg-black py-3 px-4 text-xl text-white
  dark:bg-white dark:text-black
  dark:hover:bg-white
  hover:bg-blue-600 focus:outline-none
  focus:ring-offset-2">
    Send Password Reset Email
</button>
</form>

</div>
